import React, { useState, useEffect } from 'react';
import axios from 'axios';

function App() {
  const [questions, setQuestions] = useState([]);
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
  const [selectedOption, setSelectedOption] = useState('');
  const [score, setScore] = useState(0);
  const [quizTime, setQuizTime] = useState(0);
  const [quizTimer, setQuizTimer] = useState(null);

  useEffect(() => {
    axios.get('http://localhost:5000/api/questions')
      .then(response => {
        setQuestions(response.data);
        setQuizTime(response.data.length * 60); // 1 minute per MCQ
      })
      .catch(error => {
        console.error(error);
      });
  }, []);

  useEffect(() => {
    if (quizTime === 0) {
      clearInterval(quizTimer);
      handleFinishQuiz();
    }
  }, [quizTime]);

  const handleOptionSelect = (option) => {
    setSelectedOption(option);
  };

  const handleNextQuestion = () => {
    const currentQuestion = questions[currentQuestionIndex];
    if (currentQuestion.correctOption === selectedOption) {
      setScore(score + 1);
    }
    setSelectedOption('');
    setCurrentQuestionIndex(currentQuestionIndex + 1);
  };

  const handleFinishQuiz = () => {
    const currentQuestion = questions[currentQuestionIndex];
    if (currentQuestion.correctOption === selectedOption) {
      setScore(score + 1);
    }
    clearInterval(quizTimer);
    alert(`Your score is ${score}/${questions.length}. Time taken: ${quizTime} seconds.`);
    setSelectedOption('');
    setCurrentQuestionIndex(0);
    setScore(0);
    setQuizTime(0);
  };

  const startQuizTimer = () => {
    setQuizTimer(setInterval(() => {
      setQuizTime(quizTime => quizTime - 1);
    }, 1000));
  }

  return (
    <div>
      {currentQuestionIndex < questions.length ? (
        <div>
          <h1>{questions[currentQuestionIndex].question}</h1>
          <ul>
            {questions[currentQuestionIndex].options.map((option, index) => (
              <li key={index}>
                <input
                  type="radio"
                  name="option"
                  id={`option${index}`}
                  value={option}
                  checked={selectedOption === option}
                  onChange={() => handleOptionSelect(option)}
                />
                <label htmlFor={`option${index}`}>{option}</label>
              </li>
            ))}
          </ul>
          <button onClick={handleNextQuestion} disabled={!selectedOption}>Next</button>
          {quizTime > 0 && !quizTimer && <button onClick={startQuizTimer}>Start Quiz</button>}
          <p>Time left: {quizTime} seconds</p>
        </div>
      ) : (
        <div>
          <h1>Quiz Complete</h1>
          <p>Your score is {score}/{questions.length}. Time taken: {quizTime} seconds.</p>
          <button onClick={handleFinishQuiz}>Finish</button>
        </div>
      )}
    </div>
  );
}

export default App;
