                      </button> 
                      <button
                        type="submit"
                        className="btn btn-primary  inputbtn mt-3"
                        onClick={this.fun1}
                      >
                        Submit
                      </button> 
                </div>
              </form>
            </div>
          </div>
            </div>
          </div >
        )
  }
      </React.Fragment>
    );
  }
