import React, { useEffect, useRef, useState } from 'react';
import Chart from 'chart.js/auto';
import './App.css';

const Results = () => {
  const canvasRef1 = useRef(null);
  const canvasRef2 = useRef(null);
  const [chemistryResults, setChemistryResults] = useState(null);
  const [physicsResults, setPhysicsResults] = useState(null);

  useEffect(() => {
    let chemistryResultsChart, chemistryPercentageChart, physicsResultsChart, physicsPercentageChart;
    async function fetchData() {
      try {
        const chemistryRes = await fetch('http://localhost:5000/results/chemistry');
        const chemistryData = await chemistryRes.json();
        setChemistryResults(chemistryData);

        const physicsRes = await fetch('http://localhost:5000/results/physics');
        const physicsData = await physicsRes.json();
        setPhysicsResults(physicsData);

        if (canvasRef1.current) {
          const chemistryChartData = {
            labels: ['Obtained Marks'],
            datasets: [
              {
                data: [chemistryData.obtainedMarks],
                backgroundColor: ['#36a2eb'],
              },
            ],
          };

          // Create bar chart for chemistry results
          if (chemistryResultsChart) {
            chemistryResultsChart.destroy();
          }
          chemistryResultsChart = new Chart(canvasRef1.current, {
            type: 'bar',
            data: chemistryChartData,
            options: {
              scales: {
                y: {
                  beginAtZero: true,
                  max: chemistryData.totalMarks,
                },
              },
              plugins: {
                legend: {
                  display: false,
                },
              },
            },
          });

          const chemistryPercentageData = {
            labels: ['This much you are skillful', 'This much you are lacking'],
            datasets: [
              {
                data: [chemistryData.percentage, 100 - chemistryData.percentage],
                backgroundColor: ['#36a2eb', '#ebed36'],
              },
            ],
          };

          // Create doughnut chart for percentage of chemistry
          if (chemistryPercentageChart) {
            chemistryPercentageChart.destroy();
          }
          chemistryPercentageChart = new Chart(document.getElementById('chemistryPercentageChart'), {
            type: 'doughnut',
            data: chemistryPercentageData,
            options: {
              plugins: {
                legend: {
                  position: 'bottom',
                },
              },
            },
          });
        }

        if (canvasRef2.current) {
          const physicsChartData = {
            labels: ['Obtained Marks'],
            datasets: [
              {
                data: [physicsData.obtainedMarks],
                backgroundColor: ['#36a2eb'],
              },
            ],
          };

          // // Create bar chart for physics results
          // if (physicsResultsChart) {
          //   physicsResultsChart.destroy();
          // }
          // physicsResultsChart = new Chart(canvasRef2.current, {
          //   type: 'bar',
          //   data: physicsChartData,
          //   options: {
          //     scales: {
          //       y: {
          //         beginAtZero: true,
          //         max: physicsData.totalMarks,
          //       },
          //     },
          //     plugins: {
          //       legend: {
          //         display: false,
          //       },
          //     },
          //   });

          const physicsPercentageData = {
            labels: ['This much you are skillful', 'This much you are lacking'],
            datasets: [
              {
                data: [physicsData.percentage, 100 - physicsData.percentage],
                backgroundColor: ['#36a2eb', '#ebed36'],
              },
            ],
          };

          // Create doughnut chart for percentage of physics
          if (physicsPercentageChart) {
            physicsPercentageChart.destroy();
          }
          physicsPercentageChart = new Chart(document.getElementById('physicsPercentageChart'), {
            type: 'doughnut',
            data: physicsPercentageData,
            options: {
              plugins: {
                legend: {
                  position: 'bottom',
                },
              },
            },
          });
        }
      } catch (error) {
        console.log(error);
      }
    }
    fetchData();

    // Cleanup
    return () => {
      if (chemistryResultsChart) {
        chemistryResultsChart.destroy();
      }
      if (chemistryPercentageChart) {
        chemistryPercentageChart.destroy();
      }
      if (physicsResultsChart) {
        physicsResultsChart.destroy();
      }
      if (physicsPercentageChart) {
        physicsPercentageChart.destroy();
      }
    };
  }, []);
  return (
    <div className='results'>
      <div className='results-chart'>
        <h3>Chemistry Results</h3>
        <canvas ref={canvasRef1}></canvas>
        <canvas id='chemistryPercentageChart'></canvas>
      </div>
      <div className='results-chart'>
        <h3>Physics Results</h3>
        <canvas ref={canvasRef2}></canvas>
        <canvas id='physicsPercentageChart'></canvas>
      </div>
    </div>
  );
};

export default Results;
