import React, { useEffect, useRef, useState } from 'react';
import Chart from 'chart.js/auto';

const ChemistryPieChart = () => {
  const chartRef = useRef(null);
  const [chartInstance, setChartInstance] = useState(null);
  const [chartData, setChartData] = useState({});

  useEffect(() => {
    async function fetchData() {
      try {
        const res = await fetch('/results/chemistry');
        const data = await res.json();
        setChartData({
          labels: ['Obtained Marks', 'Total Marks'],
          datasets: [
            {
              data: [data.obtainedMarks, data.totalMarks - data.obtainedMarks],
              backgroundColor: ['#36a2eb', '#ebed36'],
            },
          ],
        });

        // Check if there's an existing Chart instance and destroy it
        if (chartInstance) {
          chartInstance.destroy();
        }

        // Create a new Chart instance
        const newChartInstance = new Chart(chartRef.current, {
          type: 'doughnut',
          data: chartData,
          options: {
            plugins: {
              legend: {
                position: 'bottom',
              },
            },
          },
        });

        setChartInstance(newChartInstance);
      } catch (err) {
        console.error(err);
      }
    }

    fetchData();
  }, [chartData]);

  return (
    <div>
      <h2>Chemistry All Quizzes Average</h2>
      <canvas ref={chartRef} width="400" height="400" />
    </div>
  );
};

export default ChemistryPieChart;
