import React, { useState, useEffect } from 'react';
import '../ProfileCard.css'; // Import the CSS file for styling
import axios from 'axios';

const ProfileCard = () => {
  const [students, setStudents] = useState([]);

  useEffect(() => {
    fetchStudents();
  }, []);

  const fetchStudents = async () => {
    try {
      const response = await axios.get('/students'); // Replace '/students' with your actual API endpoint for fetching all students
      setStudents(response.data);
    } catch (error) {
      console.error('Error fetching students:', error);
    }
  };

  const renderProfileCards = () => {
    return students.map((student) => (
      <div key={student._id} className="profile-card">
        <div className="avatar">
          <img src={student.profilePic} alt="Profile Avatar" />
        </div>
        <div className="marks-section">
          <div className="marks">
            <p>Matric Marks:</p>
            <span>{student.ObtainedMatricMarks}</span>
          </div>
          <div className="marks">
            <p>Inter Marks:</p>
            <span>{student.ObtainedInterMarks}</span>
          </div>
        </div>
        <div className="quizzes-percentage">
          <p>Quizzes Percentage:</p>
          <span>{student.quizzesPercentage}%</span>
        </div>
      </div>
    ));
  };

  return (
    <div className="profile-cards-container">
      <div className="profile-cards-grid">{renderProfileCards()}</div>
    </div>
  );
};

export default ProfileCard;
