import React, { useState, useEffect } from 'react';
import axios from 'axios';

function App() {
  const [questions, setQuestions] = useState([]);
  const [selectedSubject, setSelectedSubject] = useState('');
  const [selectedAnswers, setSelectedAnswers] = useState({});
  const [score, setScore] = useState(0);

  useEffect(() => {
    if (selectedSubject) {
      axios.get(`http://localhost:127.0.0.1/Testing/${selectedSubject}`)
        .then(response => setQuestions(response.data))
        .catch(error => console.log(error));
    }
  }, [selectedSubject]);

  const handleSubjectChange = (event) => {
    setSelectedSubject(event.target.value);
    setSelectedAnswers({});
    setScore(0);
  }

  const handleAnswerChange = (event, questionId) => {
    setSelectedAnswers({
      ...selectedAnswers,
      [questionId]: event.target.value
    });
  }

  const handleSubmit = (event) => {
    event.preventDefault();
    let newScore = 0;
    questions.forEach(question => {
      if (question.correctOption === selectedAnswers[question._id]) {
        newScore += 1;
      }
    });
    setScore(newScore);
  }

  return (
    <div className="App">
      <h1>Quiz App</h1>
      <form onSubmit={handleSubmit}>
        <label htmlFor="subject">Select subject:</label>
        <select id="subject" onChange={handleSubjectChange}>
          <option value="">--Select a subject--</option>
          <option value="Chemistry">Chemistry</option>
          <option value="Physics">Physics</option>
          <option value="Maths">Maths</option>
        </select>
        {questions.map(question => (
          <div key={question._id}>
            <p>{question.question}</p>
            {question.options.map((option, index) => (
              <div key={index}>
                <label>
                  <input
                    type="radio"
                    name={question._id}
                    value={option}
                    onChange={(event) => handleAnswerChange(event, question._id)}
                    checked={selectedAnswers[question._id] === option}
                  />
                  {option}
                </label>
              </div>
            ))}
          </div>
        ))}
        <button type="submit">Submit</button>
      </form>
      {score !== 0 && <p>Your score is: {score}</p>}
    </div>
  );
}

export default App;
