import React, { useState } from 'react';
import first from './a.pdf';
import second from './b.pdf';

const PDFViewer = () => {
  const [selectedPDF, setSelectedPDF] = useState(null);

  const handleButtonClick = (pdf) => {
    setSelectedPDF(pdf);
  };

  return (
    <div>
      <button onClick={() => handleButtonClick(first)}>Show first PDF</button>
      <button onClick={() => handleButtonClick(second)}>Show second PDF</button>
      {selectedPDF && (
        <iframe src={selectedPDF} title="PDF Viewer" style={{ width: '100%', height: '600px' }}></iframe>
      )}
    </div>
  );
};

export default PDFViewer;
