import React, { useState, useEffect } from 'react';
import axios from 'axios';

function App() {
  const [questions, setQuestions] = useState([]);
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
  const [selectedOption, setSelectedOption] = useState('');
  const [score, setScore] = useState(0);
  const [time, setTime] = useState(0);
  const [intervalId, setIntervalId] = useState(null);

  useEffect(() => {
    axios.get('http://localhost:5000/api/questions')
      .then(response => {
        setQuestions(response.data);
        setTime(response.data.length * 60); // set time based on number of questions
      })
      .catch(error => {
        console.error(error);
      });

    return () => {
      if (currentQuestionIndex >= questions.length) {
        clearInterval(intervalId);
      }
    };
  }, []);


  useEffect(() => {
    setIntervalId(
      setInterval(() => {
        setTime(prevTime => prevTime - 1);
      }, 1000)
    );

    return () => {
      clearInterval(intervalId);
    };
  }, [intervalId]);

  const handleOptionSelect = (option) => {
    setSelectedOption(option);
  };

  const handleNextQuestion = () => {
    const currentQuestion = questions[currentQuestionIndex];
    if (currentQuestion.correctOption === selectedOption) {
      setScore(score + 1);
    }
    setSelectedOption('');
    setCurrentQuestionIndex(currentQuestionIndex + 1);
  };

  const handleFinishQuiz = () => {
    const currentQuestion = questions[currentQuestionIndex];
    if (currentQuestion.correctOption === selectedOption) {
      setScore(score + 1);
    }
    clearInterval(intervalId); // stop the timer
    const totalTime = questions.length * 60 - time; // calculate total time taken
    alert(`Your score is ${score}/${questions.length} and you took ${totalTime} seconds`);
    setSelectedOption('');
    setCurrentQuestionIndex(0);
    setScore(0);
    setTime(questions.length * 60); // reset time
  };

  return (
    <div>
      <div>Time remaining: {Math.max(0, time)} seconds</div>
      {currentQuestionIndex < questions.length ? (
        <div>
          <h1>{questions[currentQuestionIndex].question}</h1>
          <ul>
            {questions[currentQuestionIndex].options.map((option, index) => (
              <li key={index}>
                <input
                  type="radio"
                  name="option"
                  id={`option${index}`}
                  value={option}
                  checked={selectedOption === option}
                  onChange={() => handleOptionSelect(option)}
                />
                <label htmlFor={`option${index}`}>{option}</label>
              </li>
            ))}
          </ul>
          <button onClick={handleNextQuestion} disabled={!selectedOption}>Next</button>
        </div>
      ) : (
        <div>
          <h1>Quiz Complete</h1>
          <p>Your score is {score}/{questions.length}</p>
          <button onClick={handleFinishQuiz}>Finish</button>
        </div>
      )}
    </div>
  );
}

export default App;
