import React, { Component } from 'react'
import Input from "../common/Input"
import '../css/signin.css';
import { Avatar } from "@mui/material";


class Signin extends Component {
    state = { 
        account:{
            Email:"",
            Password:""
        }
     } 

    handletype = (e) => {
        const account = { ...this.state.account };
        account[e.currentTarget.name] = e.currentTarget.value;
        this.setState({ account });
      };


      handlesubmit = async(e) => {
        e.preventDefault();
        //alert(this.state.account.Email+ this.state.account.Password);

        //checking admin or not 
        if(this.state.account.Email=="ali@gmail.com" && this.state.account.Password=="123"){
          window.location.href = "/profile"; 
        }

        

         //checking student exist or not
try{
      const res=await fetch("/api/student/login",{
        method:"POST",
        headers:{
          "Content-Type":"application/json"
        },
        body: JSON.stringify(this.state.account)
      });
      const studentResponse=await res.json();
      if(studentResponse.result==true)
      {
        window.location.href = "/chapter/maths";
        
      }
      
      else if(studentResponse.result==false)
      {
        alert("Your Account is Currently Block Contacat With Admin");
       
        //window.location.href="/signin";
      }

    }
    catch (error) {
      console.log( error);
      window.location.href="/signin";
    }



      //checking Counsellor exist or not
try{
  const res=await fetch("/api/counsellor/login",{
    method:"POST",
    headers:{
      "Content-Type":"application/json"
    },
    body: JSON.stringify(this.state.account)
  });
  const counsellorResponse=await res.json();
  if(counsellorResponse.result==true)
  {
    window.location.href = "/chapter/maths";
    
  }
  
  else if(counsellorResponse.result==false)
  {
    alert("Your Account is Currently Block Contacat With Admin");
   
    //window.location.href="/signin";
  }

}
catch (error) {
  console.log( error);
  window.location.href="/signin";
}
    




      };

    render() { 
        const mystyle = {
            backgroundColor: "#F5CD01",
            borderTopLeftRadius: "20px",
            borderBottomLeftRadius: "20px",
            height: "100px",
            
          };
      
          
        return (
            <React.Fragment>
               <div className="fluid-container pt-4 signup_container ">
        <div
          className="shadow ps-1 m-5 bg-white signupform_radius"
          style={{height:"39vw"}}
        >
          <div className="row">
            <div className="col-sm-5 signupform_leftside" >
              <Avatar
                className="m-5"
                sx={{ width: 250, height: 250 }}
                src="images/signup.png"
              />

              <h1 className="mt-3">Let's Get You Set Up </h1>
              <p className="mt-2">
                It should take only a couple of minutes to pair with your
                watch
              </p>
            </div>
                
           

            <div id="form-col" className="col-sm-7 signupform_rightside">
              
            <form id="form" method='post' onSubmit={this.handlesubmit}>
                
                  <Input
                    label="Email"
                    value={this.state.account.Email}
                    name="Email"
                    type="email"
                    onchange={this.handletype}
                    placeholder="Email"
                  />

<Input
                    label="Password"
                    value={this.state.account.Password}
                    name="Password"
                    type="password"
                    onchange={this.handletype}
                    placeholder="Password"
                  />

                  
                  
                       
                        
                      
                    
                  

                  <button type="submit" id='button' className="btn signinbtn mt-3">
                    Sign In
                  </button>
                </form>
              </div>
            </div>
          </div>
        </div> 
            </React.Fragment>
        );
    }
}
 
export default Signin;
