import React, { useState, useEffect } from 'react';
import '../../ProfileCard.css'; // Import the CSS file for styling
import axios from 'axios';
import image from './Chemistry.jpg';

const ShowRequests = () => {
  const [requests, setRequests] = useState([]);

  useEffect(() => {
    fetchRequests();
  }, []);

  const fetchRequests = async () => {
    try {
      const counselorName = "Michael Brown";
      const response = await axios.get(`http://localhost:5000/api/requestsOfStudents/${counselorName}`);
      console.log(response.data); // Check the response data in the console
      setRequests(response.data);
    } catch (error) {
      console.error('Error fetching requests:', error);
    }
  };
  

  const renderProfileCards = () => {
    return requests.map((student, index) => (
      <div key={index} className={`profile-card animated-card-${index}`}>
        <div className="avatar">
          <img src={image} alt="Profile Avatar" />
        </div>
        <div className="student-name">{student.studentName}</div>
        <div className="request-count">Number of Requests: {student.requestCount}</div>
      </div>
    ));
  };

  return (
    <div className="profile-cards-container">
      <h2 className="page-title">Requests of Students for Tie Up</h2>
      <div className="profile-cards-grid">{renderProfileCards()}</div>
    </div>
  );
};

export default ShowRequests;
