import React, { useState } from 'react';
import { Bar, Pie } from 'react-chartjs-2';
import Chart from 'chart.js/auto';
import { CategoryScale } from 'chart.js';
import 'chartjs-adapter-date-fns';

Chart.register(CategoryScale);


useEffect(() => {
  let chartInstance = null;
  const ctx = document.getElementById('myChart').getContext('2d');

  if (chartInstance) {
    chartInstance.destroy();
  }

  chartInstance = new Chart(ctx, chartData);

  return () => {
    if (chartInstance) {
      chartInstance.destroy();
    }
  }
}, [chartData]);





const Results = () => {
  const [mathData, setMathData] = useState({
    labels: ['Math'],
    datasets: [
      {
        label: 'Math Result',
        backgroundColor: 'rgba(75,192,192,0.4)',
        borderColor: 'rgba(75,192,192,1)',
        borderWidth: 1,
        hoverBackgroundColor: 'rgba(75,192,192,0.6)',
        hoverBorderColor: 'rgba(75,192,192,1)',
        data: [80]
      }
    ]
  });

  const [chemistryData, setChemistryData] = useState({
    labels: ['Chemistry'],
    datasets: [
      {
        label: 'Chemistry Result',
        backgroundColor: 'rgba(255,99,132,0.4)',
        borderColor: 'rgba(255,99,132,1)',
        borderWidth: 1,
        hoverBackgroundColor: 'rgba(255,99,132,0.6)',
        hoverBorderColor: 'rgba(255,99,132,1)',
        data: [90]
      }
    ]
  });

  const [physicsData, setPhysicsData] = useState({
    labels: ['Physics'],
    datasets: [
      {
        label: 'Physics Result',
        backgroundColor: 'rgba(54, 162, 235, 0.4)',
        borderColor: 'rgba(54, 162, 235, 1)',
        borderWidth: 1,
        hoverBackgroundColor: 'rgba(54, 162, 235, 0.6)',
        hoverBorderColor: 'rgba(54, 162, 235, 1)',
        data: [85]
      }
    ]
  });

  const [englishData, setEnglishData] = useState({
    labels: ['English'],
    datasets: [
      {
        label: 'English Result',
        backgroundColor: 'rgba(153, 102, 255, 0.4)',
        borderColor: 'rgba(153, 102, 255, 1)',
        borderWidth: 1,
        hoverBackgroundColor: 'rgba(153, 102, 255, 0.6)',
        hoverBorderColor: 'rgba(153, 102, 255, 1)',
        data: [95]
      }
    ]
  });

  return (
    <div>
      <h2>Results</h2>
      <div style={{ display: 'flex' }}>
        <div style={{ width: '50%' }}>
          <h3>Bar Chart</h3>
          <Bar
            data={{
              labels: ['Math', 'Chemistry', 'Physics', 'English'],
              datasets: [
                {
                  label: 'Obtained Marks',
                  backgroundColor: 'rgba(75,192,192,0.4)',
                  borderColor: 'rgba(75,192,192,1)',
                  borderWidth: 1,
                  hoverBackgroundColor: 'rgba(75,192,192,0.6)',
                  hoverBorderColor: 'rgba(75,192,192,1)',
                  data: [80, 90, 85, 95]
                },
                {
                  label: 'Percentage',
                  backgroundColor: 'rgba(255, 159, 64, 0.4)',
                  borderColor: 'rgba(255, 159, 64, 1)',
                  borderWidth: 1,
                  hoverBackgroundColor: 'rgba(255, 159, 64, 0.6)',
                  hoverBorderColor: 'rgba(255, 159, 64, 1)',
                  data: [80, 90, 85, 95].map((marks) => Math.round((marks / 100) * 100))
                }
              ]
            }}
          />
        </div>
        <div style={{ width: '50%' }}>
          <h3>Pie Chart</h3>
          <Pie
            data={{
              labels: ['Math', 'Chemistry', 'Physics', 'English'],
              datasets: [
                {
                  label: 'Obtained Marks',
                  backgroundColor: ['rgba(75,192,192,0.4)', 'rgba(255, 99, 132, 0.4)', 'rgba(54, 162, 235, 0.4)', 'rgba(255, 205, 86, 0.4)'],
                  borderColor: ['rgba(75,192,192,1)', 'rgba(255, 99, 132, 1)', 'rgba(54, 162, 235, 1)', 'rgba(255, 205, 86, 1)'],
                  borderWidth: 1,
                  hoverBackgroundColor: ['rgba(75,192,192,0.6)', 'rgba(255, 99, 132, 0.6)', 'rgba(54, 162, 235, 0.6)', 'rgba(255, 205, 86, 0.6)'],
                  hoverBorderColor: ['rgba(75,192,192,1)', 'rgba(255, 99, 132, 1)', 'rgba(54, 162, 235, 1)', 'rgba(255, 205, 86, 1)'],
                  data: [80, 90, 85, 95]
                }
              ]
            }}
          />
        </div>
      </div>
    </div>
  );
};

export default Results;
