import React, { useState, useEffect } from 'react';
import '../../ProfileCard.css'; // Import the CSS file for styling
import axios from 'axios';
import image from './Chemistry.jpg';

const ShowRequests = () => {
    const [requests, setRequests] = useState([]); // Update variable name to 'requests'
  //const counselorName = getCounselorNameFromCookies(); // Function to get the counselor's name from cookies
  const counselorName = "Michael Brown"

  useEffect(() => {
    fetchStudents();
  }, []);

  const fetchStudents = async () => {
    try {
      const response = await axios.get(`http://localhost:5000/api/requestsOfStudents/${counselorName}`);
      setRequests(response.data); // Update state variable to 'setRequests'
    } catch (error) {
      console.error('Error fetching students:', error);
    }
  };

  return requests.map((student, index) => (
    <div key={student._id} className={`profile-card animated-card-${index}`}>
      <div className="avatar">
        <img src={image} alt="Profile Avatar" />
      </div>
      <div className="student-name">{student.FirstName}</div>
      <div className="marks-section">
        <div className="marks-row">
          <div className="marks">
            <p className="mark-title">Matric Marks:</p>
            <span className="mark-value">{student.ObtainedMatricMarks}</span>
          </div>
          <div className="marks">
            <p className="mark-title">Inter Marks:</p>
            <span className="mark-value">{student.ObtainedInterMarks}</span>
          </div>
          <div className="marks">
            <p className="mark-title">Quizzes Percentage:</p>
            <span className="mark-value">{student.quizzesPercentage}%</span>
          </div>
        </div>
      </div>
    </div>
  )); 
  return (
    <div className="profile-cards-container">
      <h2 className="page-title">Requests of Students for Tie Up</h2>
      <div className="profile-cards-grid">{renderProfileCards()}</div>
    </div>
  );
};

export default ShowRequests;
