import React, { Component } from "react";
import Input from "../../common/Input";
import { Avatar } from "@mui/material";
import "../../css/inpu.css";

class Student_Signup extends Component {
  state = {
    account: {
      email: "",
      password: "",
      firstname: "",
      lastname: "",
      phoneno: "",
      religion: "",
      matric_marks: "",
      inter_marks: "",
      father_income: "",
      total_matric_marks: "",
      total_inter_marks: "",
      address: "",
    },
    step: 1,
    errors: {},
  };

  handletype = (e) => {
    const account = { ...this.state.account };
    if (e.currentTarget.name === "gender") {
      account["gender"] = e.currentTarget.value;
    } else {
      account[e.currentTarget.name] = e.currentTarget.value;
    }
    this.setState({ account });
  };

  handlesubmit = (e) => {
    e.preventDefault();
    const errors = this.validateForm();
    if (Object.keys(errors).length === 0) {
      this.setState({ step: 0, errors: {} });
    } else {
      this.setState({ errors });
    }
  };

  fun1 = () => {
    this.setState({ ...this.state, step: 0, errors: {} });
  };

  validateForm = () => {
    const { firstname, lastname, phoneno, email, password, address } = this.state.account;
    const errors = {};

    if (!firstname.trim()) {
      errors.firstname = "First Name is required";
    }

    if (!lastname.trim()) {
      errors.lastname = "Last Name is required";
    }

    if (!phoneno.trim()) {
      errors.phoneno = "Phone Number is required";
    }

    if (!email.trim()) {
      errors.email = "Email is required";
    } else if (!this.isValidEmail(email)) {
      errors.email = "Invalid Email";
    }

    if (!password.trim()) {
      errors.password = "Password is required";
    } else if (password.length < 8) {
      errors.password = "Password must be at least 8 characters long";
    } else if (!this.isValidPassword(password)) {
      errors.password = "Password must contain at least one uppercase letter, one lowercase letter, and one numeric character";
    }

    if (!address.trim()) {
      errors.address = "Address is required";
    }

    return errors;
  };

  isValidEmail = (email) => {
    // Validate email using regular expression or any other validation logic
    // Return true if email is valid, false otherwise
  };

  isValidPassword = (password) => {
    // Validate password using regular expression or any other validation logic
    // Return true if password is valid, false otherwise
  };

  render() {
    const mystyle = {
      backgroundColor: "#F5CD01",
      borderTopLeftRadius: "50px",
      borderBottomLeftRadius: "40px",
      height: "39vw",
    };

    const { step, errors } = this.state;
    const { firstname, lastname, phoneno, email, password, address } = this.state.account;

    return (
      <React.Fragment>
        {step ? (
          <div className="fluid-container pt-4 signup_container">
            <div className="shadow ps-1 m-5 bg-white signupform_radius" style={{ height: "39vw" }}>
              <div className="row">
                <div className="col-sm-5 signupform_leftside">
                  <Avatar className="m-5" sx={{ width: 230, height: 230 }} src="images/signup.png" />
                  <h2 className="mt-3 signupHeading">Let's Get You Set Up </h2>
                  <p className="mt-2 signupPtag">It should take only a couple of minutes to pair with your watch</p>
                </div>

                <div id="form-col" className="col-sm-7 signupform_rightside">
                  <form id="form" onSubmit={this.handlesubmit}>
                    <Input
                      label="First Name"
                      value={firstname}
                      name="firstname"
                      type="text"
                      onchange={this.handletype}
                      placeholder="First Name"
                      error={errors.firstname}
                    />

                    <Input
                      label="Last Name"
                      value={lastname}
                      name="lastname"
                      type="text"
                      onchange={this.handletype}
                      placeholder="Last Name"
                      error={errors.lastname}
                    />

                    <Input
                      label="Phone Number"
                      value={phoneno}
                      name="phoneno"
                      type="tel"
                      onchange={this.handletype}
                      placeholder="Phone Number"
                      error={errors.phoneno}
                    />

                    <Input
                      label="Email"
                      value={email}
                      name="email"
                      type="email"
                      onchange={this.handletype}
                      placeholder="Email"
                      error={errors.email}
                    />

                    <Input
                      label="Password"
                      value={password}
                      name="password"
                      type="password"
                      onchange={this.handletype}
                      placeholder="Password"
                      error={errors.password}
                    />

                    <Input
                      label="Address"
                      value={address}
                      name="address"
                      type="address"
                      onchange={this.handletype}
                      placeholder="Address"
                      error={errors.address}
                    />

                    <div className="signup_button">
                      <button type="button" className="btn btn-primary inputbtn mt-3" onClick={this.fun1}>
                        Back
                      </button>
                      <button type="submit" className="btn btn-primary  inputbtn mt-3" onClick={this.fun1}>
                        Next
                      </button>
                    </div>
                  </form>
                </div>
              </div>
            </div>
          </div>
        ) : (
          <div className="fluid-container pt-4 signup_container">
            <div className="shadow ps-1 m-5 bg-white signupform_radius" style={{ height: "39vw" }}>
              <div className="row">
                <div className="col-sm-5 signupform_leftside">
                  <Avatar className="m-5" sx={{ width: 230, height: 230 }} src="images/signup.png" />
                  <h2 className="mt-3 signupHeading">Let's Get You Set Up </h2>
                  <p className="mt-2 signupPtag">It should take only a couple of minutes to pair with your watch</p>
                </div>

                <div id="form-col" className="col-sm-7 signupform_rightside">
                  <form id="form" onSubmit={this.handlesubmit}>
                    <Input
                      label="Matric Marks"
                      value={this.state.account.matric_marks}
                      name="matric_marks"
                      type="number"
                      onchange={this.handletype}
                      placeholder="Matric Marks"
                    />

                    <Input
                      label="Total Matric Marks"
                      value={this.state.account.total_matric_marks}
                      name="total_matric_marks"
                      type="number"
                      onchange={this.handletype}
                      placeholder="Total Matric Marks"
                    />

                    <Input
                      label="Inter Marks"
                      value={this.state.account.inter_marks}
                      name="inter_marks"
                      type="number"
                      onchange={this.handletype}
                      placeholder="Inter Marks"
                    />

                    <Input
                      label="Total Inter Marks"
                      value={this.state.account.total_inter_marks}
                      name="total_inter_marks"
                      type="number"
                      onchange={this.handletype}
                      placeholder="Total Inter Marks"
                    />

                    <Input
                      label="Father Income"
                      value={this.state.account.father_income}
                      name="father_income"
                      type="number"
                      onchange={this.handletype}
                      placeholder="Father Income"
                    />

                    <div className="signup_button">
                      <button type="button" className="btn btn-primary inputbtn mt-3" onClick={this.fun1}>
                        Back
                      </button>
                      <button type="submit" className="btn btn-primary  inputbtn mt-3" onClick={this.fun1}>
                        Submit
                      </button>
                    </div>
                  </form>
                </div>
              </div>
            </div>
          </div>
        )}
      </React.Fragment>
    );
  }
}

export default Student_Signup;
