import React, { useState, useEffect } from 'react';
import axios from 'axios';

function App() {
  const [selectedSubject, setSelectedSubject] = useState('');
  const [questions, setQuestions] = useState([]);
  const [answers, setAnswers] = useState({});
  const [score, setScore] = useState(0);

  useEffect(() => {
    if (selectedSubject) {
      axios.get(`http://localhost:5000/api/questions/${selectedSubject}`)
        .then(response => {
          setQuestions(response.data);
          setAnswers({});
          setScore(0);
        })
        .catch(error => {
          console.error(error);
        });
    }
  }, [selectedSubject]);

  const handleChange = (event) => {
    setSelectedSubject(event.target.value);
  };

  const handleAnswerChange = (event, question) => {
    const newAnswers = { ...answers };
    newAnswers[question._id] = event.target.value;
    setAnswers(newAnswers);
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    const subject = "chemistry"; // replace with the selected subject
    const answers = questionList.map(question => {
      return {
        questionId: question._id,
        selectedOption: question.selectedOption,
      };
    });
    axios.post(`http://localhost:5000/api/submit-answers/${subject}`, answers)
      .then(response => {
        setScore(response.data.score);
      })
      .catch(error => {
        console.error(error);
      });
  };



  return (
    <div>
      <h1>Quiz App</h1>
      <form onSubmit={handleSubmit}>
        <label>
          Select a subject:
          <select value={selectedSubject} onChange={handleChange}>
            <option value="">--Please choose a subject--</option>
            <option value="maths">Maths</option>
            <option value="chemistry">Chemistry</option>
            <option value="physics">Physics</option>
          </select>
        </label>
        <button type="submit" disabled={!selectedSubject}>Start Quiz</button>
      </form>
      {questions.length === 0 ? null : (
        <>
          <h2>Questions</h2>
          <form onSubmit={handleSubmit}>
            <ul>
              {questions.map(question => (
                <li key={question._id}>
                  <h3>{question.question}</h3>
                  <ul>
                    {question.options.map(option => (
                      <li key={option}>
                        <label>
                          <input type="radio" name={question._id} value={option}
                            onChange={(event) => handleAnswerChange(event, question)}
                            checked={answers[question._id] === option}
                          />
                          {option}
                        </label>
                      </li>
                    ))}
                  </ul>
                </li>
              ))}
            </ul>
            <button type="submit">Submit</button>
          </form>
        </>
      )}
      {score === 0 ? null : (
        <>
          <h2>Score</h2>
          <p>Your score is: {score}</p>
        </>
      )}
    </div>
  );
}

export default App;
