import React, { useState, useEffect } from 'react';
import '../ProfileCard.css'; // Import the CSS file for styling
import axios from 'axios';
import image from './Chemistry.jpg';

const ProfileCard = () => {
  const [students, setStudents] = useState([]);

  useEffect(() => {
    fetchStudents();
  }, []);

  const fetchStudents = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/students'); // Replace '/students' with your actual API endpoint for fetching all students
      setStudents(response.data);
    } catch (error) {
      console.error('Error fetching students:', error);
    }
  };

  const renderProfileCards = () => {
    return students.map((student) => (
      <div key={student._id} className="profile-card">
        <div className="avatar">
          <img src={image} alt="Profile Avatar" />
        </div>
        <div className="student-name">{student.FirstName}</div>
        <div className="marks-section">
          <div className="marks">
            <div className="mark-title">Matric Marks:</div>
            <div className="line">
              <div className="line-progress" style={{ width: `${student.ObtainedMatricMarks}%` }}></div>
            </div>
          </div>
        </div>
        <div className="marks-section">
          <div className="marks">
            <div className="mark-title">Inter Marks:</div>
            <div className="line">
              <div className="line-progress" style={{ width: `${student.ObtainedInterMarks}%` }}></div>
            </div>
          </div>
        </div>
        <div className="marks-section">
          <div className="marks">
            <div className="mark-title">Quizzes Percentage:</div>
            <div className="line">
              <div className="line-progress" style={{ width: `${student.quizzesPercentage}%` }}></div>
            </div>
          </div>
        </div>
      </div>
    )); 
  };

  return (
    <div className="profile-cards-container">
      <div className="cards-container">
        <h2 className="page-title">All Students</h2>
        <div className="profile-cards-grid">{renderProfileCards()}</div>
      </div>
    </div>
  );
};

export default ProfileCard;
