import React, { useState } from 'react';
import '../ProfileCard.css'; // Import the CSS file for styling
import image from './Chemistry.jpg';

const ProfileCard = () => {
  const [students, setStudents] = useState([]);

  const handleAddStudent = () => {
    const newStudent = {
      _id: students.length + 1,
      FirstName: '',
      ObtainedMatricMarks: '',
      ObtainedInterMarks: '',
      quizzesPercentage: '',
      CounsellorName: ''
    };
    setStudents([...students, newStudent]);
  };

  const handleInputChange = (index, field, value) => {
    const updatedStudents = [...students];
    updatedStudents[index][field] = value;
    setStudents(updatedStudents);
  };

  const renderProfileCards = () => {
    return students.map((student, index) => (
      <div
        key={student._id}
        className={`profile-card animated-card-${index}`}
      >
        <div className="avatar">
          <img src={image} alt="Profile Avatar" />
        </div>
        <div className="student-name">
          <input
            type="text"
            value={student.FirstName}
            placeholder="First Name"
            onChange={(e) =>
              handleInputChange(index, 'FirstName', e.target.value)
            }
          />
        </div>
        <div className="marks-section">
          <div className="marks-row">
            <div className="marks">
              <p className="mark-title">Matric Marks:</p>
              <input
                className="mark-value"
                type="text"
                value={student.ObtainedMatricMarks}
                placeholder="Matric Marks"
                onChange={(e) =>
                  handleInputChange(index, 'ObtainedMatricMarks', e.target.value)
                }
              />
            </div>
            <div className="marks">
              <p className="mark-title">Inter Marks:</p>
              <input
                className="mark-value"
                type="text"
                value={student.ObtainedInterMarks}
                placeholder="Inter Marks"
                onChange={(e) =>
                  handleInputChange(index, 'ObtainedInterMarks', e.target.value)
                }
              />
            </div>
            <div className="marks">
              <p className="mark-title">Quizzes Percentage:</p>
              <input
                className="mark-value"
                type="text"
                value={student.quizzesPercentage}
                placeholder="Quizzes Percentage"
                onChange={(e) =>
                  handleInputChange(index, 'quizzesPercentage', e.target.value)
                }
              />
            </div>
          </div>
          <div className="counselor-row">
            <span className="counselor-label">Counselor:</span>
            <input
              className="counselor-name"
              type="text"
              value={student.CounsellorName}
              placeholder="Counselor Name"
              onChange={(e) =>
                handleInputChange(index, 'CounsellorName', e.target.value)
              }
            />
          </div>
        </div>
      </div>
    ));
  };

  return (
    <div className="profile-cards-container">
      <h2 className="page-title">All Students</h2>
      <button onClick={handleAddStudent}>Add Student</button>
      <div className="profile-cards-grid">{renderProfileCards()}</div>
    </div>
  );
};

export default ProfileCard;
