import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Bar, Pie } from 'react-chartjs-2';

const Result = () => {
  const [chemistryResult, setChemistryResult] = useState({});
  const [physicsResult, setPhysicsResult] = useState({});

  useEffect(() => {
    axios.get('http://localhost/api/chemistryresult').then((res) => {
      setChemistryResult(res.data);
    });

    axios.get('http://localhost/api/physicsresult').then((res) => {
      setPhysicsResult(res.data);
    });
  }, []);

  const barData = {
    labels: ['Chemistry', 'Physics'],
    datasets: [
      {
        label: 'Total Marks',
        backgroundColor: ['rgba(255,99,132,0.2)', 'rgba(54,162,235,0.2)'],
        borderColor: ['rgba(255,99,132,1)', 'rgba(54,162,235,1)'],
        borderWidth: 1,
        hoverBackgroundColor: ['rgba(255,99,132,0.4)', 'rgba(54,162,235,0.4)'],
        hoverBorderColor: ['rgba(255,99,132,1)', 'rgba(54,162,235,1)'],
        data: [chemistryResult.totalMarks, physicsResult.totalMarks],
      },
      {
        label: 'Obtained Marks',
        backgroundColor: ['rgba(255,206,86,0.2)', 'rgba(75,192,192,0.2)'],
        borderColor: ['rgba(255,206,86,1)', 'rgba(75,192,192,1)'],
        borderWidth: 1,
        hoverBackgroundColor: ['rgba(255,206,86,0.4)', 'rgba(75,192,192,0.4)'],
        hoverBorderColor: ['rgba(255,206,86,1)', 'rgba(75,192,192,1)'],
        data: [chemistryResult.obtainedMarks, physicsResult.obtainedMarks],
      },
    ],
  };

  const pieData = {
    labels: ['Chemistry', 'Physics'],
    datasets: [
      {
        label: 'Marks Distribution',
        backgroundColor: ['rgba(255,99,132,0.2)', 'rgba(54,162,235,0.2)'],
        borderColor: ['rgba(255,99,132,1)', 'rgba(54,162,235,1)'],
        borderWidth: 1,
        hoverBackgroundColor: ['rgba(255,99,132,0.4)', 'rgba(54,162,235,0.4)'],
        hoverBorderColor: ['rgba(255,99,132,1)', 'rgba(54,162,235,1)'],
        data: [chemistryResult.obtainedMarks, physicsResult.obtainedMarks],
      },
    ],
  };

  return (
    <div>
      <h2>Result</h2>
      <div style={{ display: 'flex', justifyContent: 'space-between' }}>
        <div style={{ width: '50%' }}>
          <h3>Bar Chart</h3>
          <Bar data={barData} />
        </div>
        <div style={{ width: '50%' }}>
          <h3>Pie Chart</h3>
          <Pie data={pieData} />
        </div>
      </div>
    </div>
  );
};

export default Result;
