import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Bar, Pie } from 'react-chartjs-2';

const Results = () => {
  const [chemistryData, setChemistryData] = useState({});
  const [physicsData, setPhysicsData] = useState({});

  useEffect(() => {
    const getChemistryResults = async () => {
      try {
        const res = await axios.get('/results/chemistry');
        const { totalMarks, obtainedMarks, percentage } = res.data;
        const data = {
          labels: ['Total Marks', 'Obtained Marks'],
          datasets: [
            {
              label: 'Chemistry Result',
              backgroundColor: ['rgba(75,192,192,0.4)', 'rgba(75,192,192,0.4)'],
              borderColor: ['rgba(75,192,192,1)', 'rgba(75,192,192,1)'],
              borderWidth: 1,
              hoverBackgroundColor: ['rgba(75,192,192,0.6)', 'rgba(75,192,192,0.6)'],
              hoverBorderColor: ['rgba(75,192,192,1)', 'rgba(75,192,192,1)'],
              data: [totalMarks, obtainedMarks]
            }
          ]
        };
        setChemistryData(data);
      } catch (err) {
        console.error(err);
      }
    };

    const getPhysicsResults = async () => {
      try {
        const res = await axios.get('/results/physics');
        const { totalMarks, obtainedMarks, percentage } = res.data;
        const data = {
          labels: ['Total Marks', 'Obtained Marks'],
          datasets: [
            {
              label: 'Physics Result',
              backgroundColor: ['rgba(255,99,132,0.4)', 'rgba(255,99,132,0.4)'],
              borderColor: ['rgba(255,99,132,1)', 'rgba(255,99,132,1)'],
              borderWidth: 1,
              hoverBackgroundColor: ['rgba(255,99,132,0.6)', 'rgba(255,99,132,0.6)'],
              hoverBorderColor: ['rgba(255,99,132,1)', 'rgba(255,99,132,1)'],
              data: [totalMarks, obtainedMarks]
            }
          ]
        };
        setPhysicsData(data);
      } catch (err) {
        console.error(err);
      }
    };

    getChemistryResults();
    getPhysicsResults();
  }, []);

  return (
    <div>
      <h2>Chemistry Results</h2>
      <Pie data={chemistryData} />
      <Bar data={chemistryData} />
      <h2>Physics Results</h2>
      <Pie data={physicsData} />
      <Bar data={physicsData} />
    </div>
  );
};

export default Results;
