import React, { useState } from 'react';
import axios from 'axios';

const Quiz = () => {
  const [subject, setSubject] = useState('');
  const [questions, setQuestions] = useState([]);
  const [answers, setAnswers] = useState([]);
  const [score, setScore] = useState(null);

  const handleSubjectChange = (event) => {
    setSubject(event.target.value);
  };

  const handleSubmit = (event) => {
    event.preventDefault();

    axios.get(`http://localhost:5000/api/questions/${subject}`)
      .then(response => {
        setQuestions(response.data);
        setAnswers(response.data.map(question => ({
          questionId: question._id,
          selectedOption: null
        })));
        setScore(null);
      })
      .catch(error => {
        console.error(error);
      });
  };

  const handleOptionChange = (questionId, option) => {
    setAnswers(answers.map(answer => {
      if (answer.questionId === questionId) {
        return {
          questionId: answer.questionId,
          selectedOption: option
        };
      } else {
        return answer;
      }
    }));
  };

  const handleScoreSubmit = (event) => {
    event.preventDefault();

    axios.post(`http://localhost:5000/api/submit-answers/${subject}`, answers)
      .then(response => {
        setScore(response.data.score);
      })
      .catch(error => {
        console.error(error);
      });
  };

  return (
    <div>
      <form onSubmit={handleSubmit}>
        <label htmlFor="subject">Choose a subject:</label>
        <select id="subject" name="subject" value={subject} onChange={handleSubjectChange}>
          <option value="">--Please choose a subject--</option>
          <option value="maths">Maths</option>
          <option value="chemistry">Chemistry</option>
          <option value="physics">Physics</option>
        </select>
        <button type="submit">Start Quiz</button>
      </form>
      {questions.length > 0 && (
        <div>
          <h2>{subject} Quiz</h2>
          <form onSubmit={handleScoreSubmit}>
            {questions.map(question => (
              <div key={question._id}>
                <p>{question.question}</p>
                {question.options.map(option => (
                  <label key={option}>
                    <input type="radio" name={question._id} value={option} checked={answers.find(answer => answer.questionId === question._id).selectedOption === option} onChange={() => handleOptionChange(question._id, option)} />
                    {option}
                  </label>
                ))}
              </div>
            ))}
            <button type="submit">Submit Answers</button>
          </form>
          {score !== null && (
            <p>Your score: {score}/{questions.length}</p>
          )}
        </div>
      )}
    </div>
  );
};

export default Quiz;
