import React, { useEffect, useRef, useState } from 'react';
import Chart from 'chart.js/auto';
import './App.css';

const ChemistryResults = () => {
  const canvasRef1 = useRef(null);
  const [chemistryResults, setChemistryResults] = useState(null);

  useEffect(() => {
    async function fetchData() {
      try {
        const chemistryRes = await fetch('http://localhost:5000/results/chemistry');
        const chemistryData = await chemistryRes.json();
        setChemistryResults(chemistryData);

        const chemistryChartData = {
          labels: ['Obtained Marks'],
          datasets: [
            {
              data: [chemistryData.obtainedMarks],
              backgroundColor: ['#36a2eb'],
            },
          ],
        };

        const chemistryPercentageData = {
          labels: ['This much you are skillful', 'This much you are lacking'],
          datasets: [
            {
              data: [chemistryData.percentage, 100 - chemistryData.percentage],
              backgroundColor: ['#36a2eb', '#ebed36'],
            },
          ],
        };

        // Create doughnut chart for percentage of chemistry
        const chemistryPercentageChart = new Chart(document.getElementById('percentageChart'), {
          type: 'doughnut',
          data: chemistryPercentageData,
          options: {
            plugins: {
              legend: {
                position: 'bottom',
              },
            },
          },
        });

        // Create bar chart for chemistry results
        const chemistryResultsChart = new Chart(canvasRef1.current, {
          type: 'bar',
          data: chemistryChartData,
          options: {
            scales: {
              y: {
                beginAtZero: true,
                max: chemistryData.totalMarks,
              },
            },
            plugins: {
              legend: {
                display: false,
              },
            },
          },
        });

        return () => {
          // Destroy charts on unmount
          chemistryResultsChart.destroy();
          chemistryPercentageChart.destroy();
        };
      } catch (err) {
        console.error(err);
      }
    }

    fetchData();

  }, []);

  return (
    <div>
      <h2>Chemistry Results</h2>
      {chemistryResults && (
        <div className="results-container">
          <div className="chart-container">
            <canvas ref={canvasRef1} />
          </div>
          <div className="percentage-container">
            <canvas id="percentageChart" />
          </div>
          <div className="stats-container">
            <p>Obtained Marks: {chemistryResults.obtainedMarks}</p>
            <p>Total Marks: {chemistryResults.totalMarks}</p>
            <p>Percentage: {chemistryResults.percentage.toFixed(2)}%</p>
          </div>
        </div>
      )}
    </div>
  );
};

export default ChemistryResults;
