import React, { useState, useEffect } from 'react';
import axios from 'axios';

const App = () => {
  const [subject, setSubject] = useState('');
  const [questions, setQuestions] = useState([]);
  const [answers, setAnswers] = useState([]);
  const [score, setScore] = useState(null);

  // Fetch questions by subject from the server
  useEffect(() => {
    const fetchQuestions = async () => {
      try {
        const res = await axios.get(`/api/questions/${subject}`);
        setQuestions(res.data);
      } catch (err) {
        console.error('GET questions error:', err);
      }
    };
    if (subject) {
      fetchQuestions();
    }
  }, [subject]);

  // Handle selecting an answer option for a question
  const handleSelectOption = (questionId, selectedOption) => {
    const updatedAnswers = answers.filter(answer => answer.questionId !== questionId);
    updatedAnswers.push({ questionId, selectedOption });
    setAnswers(updatedAnswers);
  };

  // Handle submitting the answers and calculating the score
  const handleSubmitAnswers = async () => {
    try {
      const res = await axios.post('/api/submit', { answers });
      setScore(res.data.score);
    } catch (err) {
      console.error('POST answers error:', err);
    }
  };

  return (
    <div className="container">
      <h1>Quiz App</h1>
      <div className="form-group">
        <label htmlFor="subject">Choose a subject:</label>
        <select
          id="subject"
          className="form-control"
          value={subject}
          onChange={e => setSubject(e.target.value)}
        >
          <option value="">Select a subject</option>
          <option value="chemistry">Chemistry</option>
          <option value="physics">Physics</option>
          <option value="maths">Maths</option>
        </select>
      </div>
      {questions.map(question => (
        <div key={question._id} className="card">
          <div className="card-body">
            <h5 className="card-title">{question.question}</h5>
            <div className="form-group">
              {question.options.map((option, index) => (
                <div key={index} className="form-check">
                  <input
                    className="form-check-input"
                    type="radio"
                    id={`${question._id}_${index}`}
                    name={`${question._id}`}
                    value={option}
                    onChange={() => handleSelectOption(question._id, option)}
                  />
                  <label className="form-check-label" htmlFor={`${question._id}_${index}`}>
                    {option}
                  </label>
                </div>
              ))}
            </div>
          </div>
        </div>
      ))}
      {subject && (
        <div className="form-group">
          <button className="btn btn-primary" onClick={handleSubmitAnswers}>
            Submit
          </button>
        </div>
      )}
      {score !== null && (
        <div className="alert alert-info" role="alert">
          Your score: {score} out of {questions.length}
        </div>
      )}
    </div>
  );
};

export default App;
