import React, { Component } from "react";
import Input from "../../common/Input";
import { Avatar } from "@mui/material";
import "../../css/inpu.css";
import "./StudentSignup.css"; // Import your custom CSS file

class Student_Signup extends Component {
  state = {
    account: {
      email: "",
      password: "",
      firstname: "",
      lastname: "",
      phoneno: "",
      gender:"",
      religion: "",
      matric_marks: "",
      inter_marks: "",
      father_income: "",
      total_matric_marks: "",
      total_inter_marks: "",
    },

  };

  handletype = (e) => {
    const account = { ...this.state.account };
    if(e.currentTarget.name==="gender"){
      account["gender"]= e.currentTarget.value   
    }else{
      account[e.currentTarget.name] = e.currentTarget.value;
    }

    this.setState({ account });

  };


   handlesubmit=async (e)=> {
   e.preventDefault();
      const obj={
      Email:this.state.account.email,
      Password:this.state.account.password,
      FirstName:this.state.account.firstname,
      LastName:this.state.account.lastname,
      PhoneNo:this.state.account.phoneno,
      Gender:this.state.account.gender,
      ObtainedMatricMarks:this.state.account.matric_marks,
      TotalMatricMarks:this.state.account.total_matric_marks,
      ObtainedInterMarks:this.state.account.inter_marks,
      TotalInterMarks:this.state.account.total_inter_marks,
      FatherIncome:this.state.account.father_income

    }
    const res=await fetch("/api/Student/AddStudent",{
      method:"POST",
      headers:{
        "Content-Type":"application/json"
      },
      body: JSON.stringify(obj)
    });
    const data=await res.json();
     alert(data.message);
    }


    fun1 =()=> {
    this.setState({...this.state,step:0})

  }

  render() {
    return (
      <React.Fragment>
        {this.state.step ? (
          <div className="container pt-4">
            <div className="custom-shadow custom-ps-1 custom-m-5 custom-bg-white custom-rounded custom-container">
              <div className="row">
                <div className="custom-col-sm-4 custom-mystyle">
                  <div className="custom-avatar-container">
                    <Avatar
                      className="custom-avatar"
                      sx={{ width: 250, height: 250 }}
                      src="images/signup.png"
                    />

                    <div className="custom-avatar-text">
                      <h1 className="custom-mt-3">Let's Get You Set Up</h1>
                      <p className="custom-mt-2">
                        It should take only a couple of minutes to pair with your watch
                      </p>
                    </div>
                  </div>
                </div>

                <div className="custom-col-sm-8">
                  <h1>Student Signup Here</h1>

                  {/* Form Started */}
                  <form id="form" method="Post" onSubmit={this.handlesubmit}>
                    <div className="custom-form-group">
                      <label className="custom-form-label">Matric Marks</label>
                      <Input
                        value={this.state.account.matric_marks}
                        name="matric_marks"
                        type="number"
                        onchange={this.handletype}
                        placeholder="Matric Marks"
                      />
                    </div>

                    <div className="custom-form-group">
                      <label className="custom-form-label">Total Matric Marks</label>
                      <Input
                        value={this.state.account.total_matric_marks}
