import React, { useState, useEffect } from 'react';
import axios from 'axios';

function App() {
  const [questions, setQuestions] = useState([]);
  const [selectedSubject, setSelectedSubject] = useState('');
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
  const [userAnswers, setUserAnswers] = useState([]);
  const [score, setScore] = useState(null);

  useEffect(() => {
    if (selectedSubject) {
      axios.get(`http://localhost:5000/api/questions/${selectedSubject}`)
        .then(response => {
          setQuestions(response.data);
        })
        .catch(error => {
          console.error(error);
        });
    }
  }, [selectedSubject]);

  function handleSubjectSelect(event) {
    setSelectedSubject(event.target.value);
    setCurrentQuestionIndex(0);
    setUserAnswers([]);
    setScore(null);
  }

  function handleAnswerSelect(answer) {
    const newAnswers = [...userAnswers];
    newAnswers[currentQuestionIndex] = answer;
    setUserAnswers(newAnswers);
  }

  function handleNextClick() {
    setCurrentQuestionIndex(currentQuestionIndex + 1);
  }

  function handlePrevClick() {
    setCurrentQuestionIndex(currentQuestionIndex - 1);
  }

  function handleSubmitClick() {
    axios.post(`http://localhost:5000/api/score/${selectedSubject}`, {
      userAnswers
    })
    .then(response => {
      setScore(response.data.score);
    })
    .catch(error => {
      console.error(error);
    });
  }

  return (
    <div>
      <h1>Quiz Questions</h1>
      <label htmlFor="subject-select">Select a subject:</label>
      <select id="subject-select" value={selectedSubject} onChange={handleSubjectSelect}>
        <option value="">--Please choose a subject--</option>
        <option value="chemistry">Chemistry</option>
        <option value="maths">Maths</option>
        <option value="physics">Physics</option>
      </select>

      {questions.length === 0 ? (
        <p>Please select a subject to begin.</p>
      ) : (
        <div>
          <h2>{questions[currentQuestionIndex].question}</h2>
          <ul>
            {questions[currentQuestionIndex].options.map((option, index) => (
              <li key={index}>
                <label>
                  <input type="radio" name="answer" value={option} onChange={() => handleAnswerSelect(option)} checked={userAnswers[currentQuestionIndex] === option} />
                  {option}
                </label>
              </li>
            ))}
          </ul>
          <button onClick={handlePrevClick} disabled={currentQuestionIndex === 0}>Previous</button>
          <button onClick={handleNextClick} disabled={currentQuestionIndex === questions.length - 1}>Next</button>
          {currentQuestionIndex === questions.length - 1 && (
            <button onClick={handleSubmitClick}>Submit</button>
          )}
          {score && (
            <p>Your score is: {score}</p>
          )}
        </div>
      )}
    </div>
  );
}

export default App;
