import React, { useState, useEffect } from 'react';
import axios from 'axios';

function App() {
  const [selectedSubject, setSelectedSubject] = useState('');
  const [questions, setQuestions] = useState([]);
  const [answers, setAnswers] = useState({});
  const [score, setScore] = useState(0);

  useEffect(() => {
    // Fetch questions for selected subject
    if (selectedSubject !== '') {
      axios.get(`http://localhost:5000/api/questions/${selectedSubject}`)
        .then(response => {
          setQuestions(response.data);
        })
        .catch(error => {
          console.error(error);
        });
    }
  }, [selectedSubject]);

  const handleSubjectChange = (event) => {
    setSelectedSubject(event.target.value);
  };

  const handleAnswerChange = (event, questionId) => {
    setAnswers(prevAnswers => ({
      ...prevAnswers,
      [questionId]: event.target.value
    }));
  };

  const handleSubmitAnswers = () => {
    // Submit answers and calculate score
    axios.post('http://localhost:5000/api/submit-answers', { answers })
      .then(response => {
        setScore(response.data.score);
      })
      .catch(error => {
        console.error(error);
      });
  };

  return (
    <div>
      <h1>Quiz App</h1>
      <div>
        <label>Select subject:</label>
        <select value={selectedSubject} onChange={handleSubjectChange}>
          <option value="">Select a subject</option>
          <option value="maths">Maths</option>
          <option value="chemistry">Chemistry</option>
          <option value="physics">Physics</option>
        </select>
      </div>
      {selectedSubject !== '' && (
        <div>
          <h2>{selectedSubject.toUpperCase()} Quiz</h2>
          {questions.length === 0 ? (
            <p>No questions found for this subject.</p>
          ) : (
            <div>
              {questions.map(question => (
                <div key={question._id}>
                  <h3>{question.question}</h3>
                  {question.options.map(option => (
                    <div key={option}>
                      <input
                        type="radio"
                        id={option}
                        name={question._id}
                        value={option}
                        checked={answers[question._id] === option}
                        onChange={(event) => handleAnswerChange(event, question._id)}
                      />
                      <label htmlFor={option}>{option}</label>
                    </div>
                  ))}
                </div>
              ))}
              <button onClick={handleSubmitAnswers}>Submit</button>
            </div>
          )}
          {score !== 0 && (
            <div>
              <h3>Your score: {score}</h3>
            </div>
          )}
        </div>
      )}
    </div>
  );
}

export default App;
