import React, { useState, useEffect } from 'react';
import axios from 'axios';

function App() {
  const [questions, setQuestions] = useState([]);
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
  const [selectedOption, setSelectedOption] = useState('');
  const [score, setScore] = useState(0);
  const [subject, setSubject] = useState('');

  useEffect(() => {
    axios.get(`http://localhost:5000/api/questions?subject=${subject}`)
      .then(response => {
        setQuestions(response.data);
      })
      .catch(error => {
        console.error(error);
      });
  }, [subject]);

  const handleOptionSelect = (option) => {
    setSelectedOption(option);
  };

  const handleNextQuestion = () => {
    const currentQuestion = questions[currentQuestionIndex];
    if (currentQuestion.correctOption === selectedOption) {
      setScore(score + 1);
    }
    setSelectedOption('');
    setCurrentQuestionIndex(currentQuestionIndex + 1);
  };

  const handleFinishQuiz = () => {
    const currentQuestion = questions[currentQuestionIndex];
    if (currentQuestion.correctOption === selectedOption) {
      setScore(score + 1);
    }
    alert(`Your score is ${score}/${questions.length}`);
    setSelectedOption('');
    setCurrentQuestionIndex(0);
    setScore(0);
  };

  return (
    <div>
      <div>
        <h2>Select a subject:</h2>
        <button onClick={() => setSubject('chemistry')}>Chemistry</button>
        <button onClick={() => setSubject('physics')}>Physics</button>
        <button onClick={() => setSubject('maths')}>Maths</button>
      </div>
      {currentQuestionIndex < questions.length ? (
        <div>
          <h1>{questions[currentQuestionIndex].question}</h1>
          <ul>
            {questions[currentQuestionIndex].options.map((option, index) => (
              <li key={index}>
                <input
                  type="radio"
                  name="option"
                  id={`option${index}`}
                  value={option}
                  checked={selectedOption === option}
                  onChange={() => handleOptionSelect(option)}
                />
                <label htmlFor={`option${index}`}>{option}</label>
              </li>
            ))}
          </ul>
          <button onClick={handleNextQuestion} disabled={!selectedOption}>Next</button>
        </div>
      ) : (
        <div>
          <h1>Quiz Complete</h1>
          <p>Your score is {score}/{questions.length}</p>
          <button onClick={handleFinishQuiz}>Finish</button>
        </div>
      )}
    </div>
  );
}

export default App;
